<script>    let i = 0; // Initialize field count globally    // Open modal and set the initial count for fields    $('.edit-modal').on('click', function () {        // Set `i` to the count of existing info fields from data attribute        i = $(this).data('infofields') || 0;        i++;    });    // Event delegation for dynamically added delete buttons    $(document).on('click', '.delete_field', function () {        $(this).closest('.field-remove-row').remove();    });    // Add new dynamic form field based on the modal type    $(document).on('click', '.add-field', function () {        console.log(i);        const container = $(this).closest('.modal').find('.field-container'); // Find the correct container within the modal        const formTemplate = `@include('backend.order.task.partial._user_data_need_fields', ['key' => '${i}'])`;        // Append the new field template to the right container        container.append(formTemplate);        i++; // Increment index for unique field identification    });</script>